import { useEffect, useState } from 'react';
import calculateMatrix from '../utils/gridGenerator';
import './MineGrid.css'

function MineGrid() {
  const mineSize = 9, dificulty = 'EASY';
  const [isBombClicked, setIsBombClicked] = useState(false);
  const [openedCellsList, setOpenedCellsList] = useState(new Array(mineSize*mineSize).fill(0));
  const [generatedGrid, updateGeneratedGrid] = useState(new Array(mineSize*mineSize));
  useEffect(() => {
    updateGeneratedGrid(calculateMatrix(mineSize, dificulty));
  }, []);

  const openSurroundingCells = (selectedIndex: number, updatedOpenedCellList: any) => {
    updatedOpenedCellList[selectedIndex] = 1;
      const isFirstRow = selectedIndex < (mineSize);
      const isLastRow = selectedIndex >= (mineSize*(mineSize-1));
      // Open cells below the selected tile
      if (!isLastRow) {
        const updatedIndex = selectedIndex+mineSize;
        updatedOpenedCellList[updatedIndex] = 1;
        (generatedGrid[updatedIndex] === 0) && (updatedOpenedCellList = openSurroundingCells(updatedIndex, updatedOpenedCellList));
        if ((updatedIndex%mineSize) !== 0) {
          updatedOpenedCellList[updatedIndex - 1] = 1;
          (generatedGrid[updatedIndex - 1] === 0) && (updatedOpenedCellList = openSurroundingCells(updatedIndex - 1, updatedOpenedCellList));
        }
        if(((updatedIndex+1)%mineSize) !== 0) {
          updatedOpenedCellList[updatedIndex + 1] = 1;
          (generatedGrid[updatedIndex + 1] === 0) && (updatedOpenedCellList = openSurroundingCells(updatedIndex + 1, updatedOpenedCellList));
        }
      }
      // Open cells above the selected tile
      if (!isFirstRow) {
        const updatedIndex = selectedIndex-mineSize;
        updatedOpenedCellList[updatedIndex] = 1;
        (generatedGrid[updatedIndex] === 0) && (updatedOpenedCellList = openSurroundingCells(updatedIndex, updatedOpenedCellList));
        if ((updatedIndex%mineSize) !== 0) {
          updatedOpenedCellList[updatedIndex - 1] = 1;
          (generatedGrid[updatedIndex - 1] === 0) && (updatedOpenedCellList = openSurroundingCells(updatedIndex - 1, updatedOpenedCellList));
        }
        if (((updatedIndex+1)%mineSize) !== 0) {
          updatedOpenedCellList[updatedIndex + 1] = 1;
          (generatedGrid[updatedIndex + 1] === 0) && (updatedOpenedCellList = openSurroundingCells(updatedIndex + 1, updatedOpenedCellList));
        }
      }
      if((selectedIndex%mineSize) !== 0) {
        updatedOpenedCellList[selectedIndex - 1] = 1;
        (generatedGrid[updatedIndex - 1] === 0) && (updatedOpenedCellList = openSurroundingCells(updatedIndex - 1, updatedOpenedCellList));
      }
      (selectedIndex%mineSize) !== 0 && (updatedOpenedCellList[selectedIndex - 1] = 1);
      ((selectedIndex+1)%mineSize) !== 0 && (updatedOpenedCellList[selectedIndex + 1] = 1);
      return updatedOpenedCellList;
  }

  const onClickCell = (selectedIndex: number) => {
    const updatedOpenedCellList = [...openedCellsList];
    if (generatedGrid[selectedIndex]) {
      updatedOpenedCellList[selectedIndex] = 1;
      setOpenedCellsList(updatedOpenedCellList)
    } else {
      setOpenedCellsList(openSurroundingCells(selectedIndex, updatedOpenedCellList));

      // setOpenedCellsList(updatedOpenedCellList);
    }
  }

  const returnMineRows = () => {
    return generatedGrid.map((gridValue: number, index: number) => (
      <div
        className={`mineTile ${openedCellsList[index] === 1 && 'not-A-Mine'} ${openedCellsList[index] === 1 && gridValue === -1 && 'mine'}`}
        onClick={() => onClickCell(index)}
      >
          {openedCellsList[index] ? gridValue : ''}
      </div>
    ))
  }


  return (
    <div className='container'>
      {returnMineRows()}
    </div>
  );
}

export default MineGrid;
