import { useEffect, useState } from "react";
import './MineGrid.css'

export default function Timer({isDarkMode}: {isDarkMode: boolean}) {
  const [timer, setTimer] = useState(0);
  const [isPaused, setPausedState] = useState(false);

  const checkTimer = () => {
    let prevTimer = 0;
    setTimer((prev) => {
      prevTimer = prev;
      return prev;
    });
  };

  useEffect(() => {
    const id = setInterval(() => {
      setTimer((prev) => prev + 1);
    }, 1000);

    return () => {
      checkTimer();
      clearInterval(id);
    };
  }, []);

  const convertToTime = () => {
    const seconds = Math.abs(timer%60);
    const minutes = Math.floor(timer/60);
    const hours = minutes < 60 ? 0 : Math.floor(minutes/60);
    return `${hours <= 9 ? `0${hours}` : hours} : ${minutes <= 9 ? `0${minutes}` : minutes} : ${seconds <= 9 ? `0${seconds}` : seconds}`;
  }


  return (
    <div className={`timer `}>
      {convertToTime()}
    </div>
  );
}
