import { useEffect, useState } from 'react';
import calculateMatrix from '../utils/gridGenerator';
import './MineGrid.css'

function MineGrid() {
  const mineSize = 9, dificulty = 'EASY';
  const [isBombClicked, setIsBombClicked] = useState(false);
  const [openedCellsList, setOpenedCellsList] = useState(new Array(mineSize*mineSize).fill(0));
  const [generatedGrid, updateGeneratedGrid] = useState(new Array(mineSize*mineSize));
  useEffect(() => {
    updateGeneratedGrid(calculateMatrix());
  }, []);

  const onClickCell = (selectedIndex: number) => {
    console.log(selectedIndex, generatedGrid[selectedIndex])
    const updatedOpenedCellList = [...openedCellsList]
    console.log()
    if (generatedGrid[selectedIndex]) {
      console.log('if', generatedGrid[selectedIndex])
      updatedOpenedCellList[selectedIndex] = 1;
      setOpenedCellsList(updatedOpenedCellList)
    } else {
      console.log('else', generatedGrid[selectedIndex])
      updatedOpenedCellList[selectedIndex] = 1;
      const isLastRow = selectedIndex >= (mineSize*(mineSize-1));
      if (!isLastRow) {
        updatedOpenedCellList[selectedIndex] = 1;
      }
      (selectedIndex%mineSize) !== 0 && (updatedOpenedCellList[selectedIndex - 1] = 1);
      ((selectedIndex+1)%mineSize) !== 0 && (updatedOpenedCellList[selectedIndex + 1] = 1);
      setOpenedCellsList(updatedOpenedCellList);


    }
  }

  const returnMineRows = () => {
    return generatedGrid.map((gridValue: number, index: number) => (
      <div className='mine' onClick={() => onClickCell(index)}>
          {openedCellsList[index] ? gridValue : ''}
      </div>
    ))
  }


  return (
    <div className='container'>
      {returnMineRows()}
    </div>
  );
}

export default MineGrid;
