  const mineSize = 9, dificulty = 'EASY';
  const [] = useState()
  const generatedGrid = calculateMatrix()
  const openedCellsList = new Array(mineSize*mineSize).fill(0)

  const onClickCell = () => {}

  const returnMineRows = () => {
    return generatedGrid.map((gridValue: number, index: number) => (
      <div className='mine' onClick={onClickCell}>{gridValue}</div>
    ))
  }


  return (
    <div className='container'>
      {returnMineRows()}
    </div>
  );
}

export default MineGrid;
