import { useEffect, useState } from "react";

export default function Timer() {
  const [timer, setTimer] = useState(0);

  const checkTimer = () => {
    let prevTimer = 0;
    setTimer((prev) => {
      prevTimer = prev;
      return prev;
    });
    console.log(prevTimer);
  };

  useEffect(() => {
    const id = setInterval(() => {
      setTimer((prev) => prev + 1);
    }, 1000);

    return () => {
      checkTimer();
      clearInterval(id);
    };
  }, []);
}
